<?php	// important * untuk application base dari halaman ini	class Dashboard extends MY_Controller {				function  __construct() {			// load application base			parent::__construct();			$this->PrivateAppBase();		}		public		function index(){			// template content			$this->smarty->assign("template_content", "private/dashboard/grafik");			//-- Highcharts --			//total album			$this->load->model('dashboardmodel');			$total_album = $this->dashboardmodel->get_total_album();			$this->smarty->assign("total_album", $total_album);			//total album			$total_foto = $this->dashboardmodel->get_total_foto();			$this->smarty->assign("total_foto", $total_foto);			//total video			$total_video = $this->dashboardmodel->get_total_video();			$this->smarty->assign("total_video", $total_video);			//total informasi			$total_informasi = $this->dashboardmodel->get_total_informasi();			$this->smarty->assign("total_informasi", $total_informasi);			//total opini					$berita = $this->dashboardmodel->get_total_berita();			$this->smarty->assign("berita", $berita);						// display document			$this->parser->parse('private/base-layout/document.html');					}	}