<?php    class Kecamatan extends MY_Controller {                function  __construct() {            // load application base            parent::__construct();            $this->PrivateAppBase();        }        public        function index() {            // template content            $this->smarty->assign("template_content", "private/kecamatan/list");            // load            $this->load->library('pagination');            $this->load->model('kecamatanmodel');            $this->load->library('notification');            $this->layout->load_javascript("js/utility/checkbox.js");            $this->layout->load_javascript("js/admin/plugins/datatables/jquery.dataTables.js");            $this->layout->load_javascript("js/admin/plugins/datatables/dataTables.bootstrap.js");            // var            $id_kecamatan = intval($this->uri->segment(4, 0));                        $data = $this->kecamatanmodel->get_data_kecamatan_all($id_kecamatan);                   $this->smarty->assign("kecamatan", $data);                 // kota            $datakota = $this->kecamatanmodel->get_all_kota();            $this->smarty->assign("kota", $datakota);            // notification            $arr_notify = $this->notification->get_notification();            $this->smarty->assign("notification_msg", $arr_notify['message']);            $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':            'green'));            // parse url            $this->smarty->assign('url_add', site_url('private/kecamatan/add/' . $id_kecamatan));            $this->smarty->assign('url_process', site_url('private/kecamatan/process/hapus'));            $this->smarty->assign('url_list', site_url('private/kecamatan/index/' . $id_kecamatan));            $this->smarty->assign('url_edit', site_url('private/kecamatan/edit/' . $id_kecamatan));            $this->smarty->assign('url_edit2', site_url('private/kecamatan/edit/'));            $this->smarty->assign('url_delete', site_url('private/kecamatan/hapus/' . $id_kecamatan));            $this->smarty->assign('url_search', site_url('private/kecamatan/index/'));            $this->smarty->assign("kota_selected", $id_kecamatan);            // display document            $this->parser->parse( 'private/base-layout/document.html');        }        public        function process($action) {            switch($action) {                case 'add':                    $this->process_add();                    break;                case 'edit':                    $this->process_edit();                    break;                case 'hapus':                    $this->process_hapus();                    break;                case 'search':                    $this->process_search();                    break;                default :                    // default redirect                    redirect('private/kecamatan/add');                    break;            }    }    public    function process_search() {        // default redirect        redirect('private/kecamatan/index/' . $this->input->post('id_kota'));    }    public    function add($id_kecamatan = '') {        // template content        $this->smarty->assign("template_content", "private/kecamatan/kecamatan-add");        //load        $this->load->model('kecamatanmodel');        $this->load->library('notification');        // url        $this->smarty->assign('url_add', site_url('private/kecamatan/add/' . $id_kecamatan));        $this->smarty->assign('url_list', site_url('private/kecamatan/index/' . $id_kecamatan));        $this->smarty->assign("url_process", site_url("private/kecamatan/process/add"));        // --        $datakota = $this->kecamatanmodel->get_all_kota();        $this->smarty->assign("kota", $datakota);        $this->smarty->assign("kota_selected", $id_kecamatan);        // notification        $arr_notify = $this->notification->get_notification();                if(!empty($arr_notify['post'])) {            $this->smarty->assign("data", $arr_notify['post']);        }        //id kecamatan        $id_kecamatan = $this->kecamatanmodel->get_max_kode_kecamatan();        $this->smarty->assign('id_kecamatan',$id_kecamatan);        $this->smarty->assign("notification_msg", $arr_notify['message']);        $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':        'green'));        // display document        $this->parser->parse( 'private/base-layout/document.html');    }    public    function process_add() {        // load library        $this->load->model('kecamatanmodel');        $this->load->library('notification');        // set rules        $this->notification->check_post('id_kecamatan', 'id_kecamatan', 'required');        $this->notification->check_post('nama_kecamatan', 'Nama kecamatan', 'required');        $nama_kecamatan = $this->input->post('nama_kecamatan');        // run                if ($this->notification->valid_input()) {                        if($this->kecamatanmodel->is_exists_kecamatan(strtolower($nama_kecamatan))) {                $this->notification->set_message('Nama kecamatan yang diinput di Provinsi ini sudah ada');                $this->notification->clear_post();            }        }                if ($this->notification->valid_input()) {            $id_kecamatan = $this->input->post('id_kecamatan');            $data = $this->kecamatanmodel->get_kecamatan_by_id($id_kecamatan);                        if(!empty($data)):            $this->notification->set_message("Kode kecamatan yang dimasukkan sudah ada!");            $this->notification->sent_notification(false);            redirect('private/kecamatan/add/' . $this->input->post('id_kecamatan'));            endif;            // params            $params = array($this->input->post('id_kecamatan'),$this->input->post('id_kecamatan'), $this->input->post('nama_kecamatan'));                        if($this->kecamatanmodel->process_kecamatan_add($params)) {                               $this->notification->clear_post();                $this->notification->set_message("Data berhasil disimpan");                $this->notification->sent_notification(true);            } else {                $this->notification->set_message("Data gagal disimpan");                $this->notification->sent_notification(false);            }        } else {            $this->notification->sent_notification(false);        }        // default redirect        redirect('private/kecamatan/add/' . $this->input->post('id_kota'));    }    public    function edit($id_kota = '', $id_kecamatan = '') {        // template content        $this->smarty->assign("template_content", "private/kecamatan/kecamatan-edit");        // load        $this->load->model('kecamatanmodel');        $this->load->library('notification');        // assign variable        // url        $this->smarty->assign('url_add', site_url('private/kecamatan/add/' . $id_kota));        $this->smarty->assign('url_list', site_url('private/kecamatan/index/' . $id_kota));        $this->smarty->assign("url_process", site_url("private/kecamatan/process/edit"));        // data        $data = $this->kecamatanmodel->get_kecamatan_by_id($id_kecamatan);        $this->smarty->assign("data", $data);        $datakota = $this->kecamatanmodel->get_all_kota();        $this->smarty->assign("kota", $datakota);        $this->smarty->assign("kota_selected", $id_kota);        // notification        $arr_notify = $this->notification->get_notification();                if(!empty($arr_notify['post'])) {            $this->smarty->assign("data", $arr_notify['post']);        }        $this->smarty->assign("notification_msg", $arr_notify['message']);        $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':        'green'));        // display document        $this->parser->parse( 'private/base-layout/document.html');    }    public    function hapus($id_kota = '', $id_kecamatan = '') {        // template content        $this->smarty->assign("template_content", "private/kecamatan/kecamatan-hapus");        // load        $this->load->model('kecamatanmodel');        $this->load->library('notification');        // assign variable        // url        $this->smarty->assign('url_add', site_url('private/kecamatan/add/' . $id_kota));        $this->smarty->assign('url_list', site_url('private/kecamatan/index/' . $id_kota));        $this->smarty->assign("url_process", site_url("private/kecamatan/process/hapus"));        // data        $data = $this->kecamatanmodel->get_kecamatan_by_id($id_kecamatan);        $this->smarty->assign("data", $data);        $datakota = $this->kecamatanmodel->get_all_kota();        $this->smarty->assign("kota", $datakota);        $this->smarty->assign("kota_selected", $id_kecamatan);        // notification        $arr_notify = $this->notification->get_notification();                if(!empty($arr_notify['post'])) {            $this->smarty->assign("data", $arr_notify['post']);        }        $this->smarty->assign("notification_msg", $arr_notify['message']);        $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':        'green'));        // display document        $this->parser->parse( 'private/base-layout/document.html');    }    public    function process_edit() {        // load library        $this->load->model('kecamatanmodel');        $this->load->library('notification');        // set rules        $this->notification->check_post('id_kecamatan', 'id_kecamatan', 'required');        $this->notification->check_post('id_kota', 'Nama kota', 'required');        $this->notification->check_post('nama_kecamatan', 'Nama kecamatan', 'required');        // run                if ($this->notification->valid_input()) {            // params            $params = array($this->input->post('id_kota'), $this->input->post('nama_kecamatan'), $this->input->post('id_kecamatan'));            // processing                        if($this->kecamatanmodel->process_kecamatan_edit($params)) {                               $this->notification->clear_post();                $this->notification->set_message("Data berhasil disimpan");                $this->notification->sent_notification(true);            } else {                $this->notification->set_message("Data gagal disimpan");                $this->notification->sent_notification(false);            }        } else {            $this->notification->sent_notification(false);        }        // default redirect        redirect('private/kecamatan/edit/' . $this->input->post('id_kota') . '/' .$this->input->post('id_kecamatan'));    }    public    function process_hapus() {        // load library        $this->load->library('notification');        $this->load->model('kecamatanmodel');        // set rules        $this->notification->check_post('id_kecamatan', 'ID kecamatan', 'required');        // run        $id = $this->input->post('id_kecamatan');        $banyak = count($id);                if ($this->notification->valid_input()) {            $data = $this->kecamatanmodel->get_kecamatan_by_id($id_kecamatan);                        if($this->kecamatanmodel->process_kecamatan_delete_one($id)) {                            $this->notification->clear_post();                $this->notification->set_message($banyak.' Data berhasil dihapus');                $this->notification->sent_notification(true);            } else {                $this->notification->set_message("Data gagal dihapus");                $this->notification->sent_notification(false);            }        } else {            $this->notification->sent_notification(false);        }        // default redirect        redirect('private/kecamatan/index/' . $this->input->post('id_kota'));    }}