<?php    class Kelurahan extends MY_Controller {                function  __construct() {            // load application base            parent::__construct();            $this->PrivateAppBase();        }        public        function index() {            // template content            $this->smarty->assign("template_content", "private/kelurahan/list");            // load            $this->load->library('pagination');            $this->load->model('kelurahanmodel');            $this->load->library('notification');            $this->layout->load_javascript("js/utility/checkbox.js");            $this->layout->load_javascript("js/admin/plugins/datatables/jquery.dataTables.js");            $this->layout->load_javascript("js/admin/plugins/datatables/dataTables.bootstrap.js");            // var            $id_kecamatan = intval($this->uri->segment(4, 0));                        $data = $this->kelurahanmodel->get_data_kelurahan_all($id_kecamatan);                   $this->smarty->assign("kelurahan", $data);                 // kota            $datakecamatan = $this->kelurahanmodel->get_all_kecamatan();            $this->smarty->assign("kecamatan", $datakecamatan);            // notification            $arr_notify = $this->notification->get_notification();            $this->smarty->assign("notification_msg", $arr_notify['message']);            $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':            'green'));            // parse url            $this->smarty->assign('url_add', site_url('private/kelurahan/add/' . $id_kecamatan));            $this->smarty->assign('url_process', site_url('private/kelurahan/process/hapus'));            $this->smarty->assign('url_list', site_url('private/kelurahan/index/' . $id_kecamatan));            $this->smarty->assign('url_edit', site_url('private/kelurahan/edit/' . $id_kecamatan));            $this->smarty->assign('url_edit2', site_url('private/kelurahan/edit/'));            $this->smarty->assign('url_delete', site_url('private/kelurahan/hapus/' . $id_kecamatan));            $this->smarty->assign('url_search', site_url('private/kelurahan/index/'));            $this->smarty->assign("kecamatan_selected", $id_kecamatan);            // display document            $this->parser->parse( 'private/base-layout/document.html');        }        public        function process($action) {            switch($action) {                case 'add':                    $this->process_add();                    break;                case 'edit':                    $this->process_edit();                    break;                case 'hapus':                    $this->process_hapus();                    break;                case 'search':                    $this->process_search();                    break;                default :                    // default redirect                    redirect('private/kelurahan/add');                    break;            }    }    public    function process_search() {        // default redirect        redirect('private/kelurahan/index/' . $this->input->post('id_kecamatan'));    }    public    function add($id_kecamatan = '') {        // template content        $this->smarty->assign("template_content", "private/kelurahan/kelurahan-add");        //load        $this->load->model('kelurahanmodel');        $this->load->library('notification');        // url        $this->smarty->assign('url_add', site_url('private/kelurahan/add/' . $id_kecamatan));        $this->smarty->assign('url_list', site_url('private/kelurahan/index/' . $id_kecamatan));        $this->smarty->assign("url_process", site_url("private/kelurahan/process/add"));        // --        $datakecamatan = $this->kelurahanmodel->get_all_kecamatan();        $this->smarty->assign("kecamatan", $datakecamatan);        $this->smarty->assign("kecamatan_selected", $id_kecamatan);        // notification        $arr_notify = $this->notification->get_notification();                if(!empty($arr_notify['post'])) {            $this->smarty->assign("data", $arr_notify['post']);        }        //id kelurahan        $id_kelurahan = $this->kelurahanmodel->get_max_kode_kelurahan();        $this->smarty->assign('id_kelurahan',$id_kelurahan);        $this->smarty->assign("notification_msg", $arr_notify['message']);        $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':        'green'));        // display document        $this->parser->parse( 'private/base-layout/document.html');    }    public    function process_add() {        // load library        $this->load->model('kelurahanmodel');        $this->load->library('notification');        // set rules        $this->notification->check_post('id_kecamatan', 'id_kecamatan', 'required');        $this->notification->check_post('nama_kelurahan', 'Nama kelurahan', 'required');        $nama_kelurahan = $this->input->post('nama_kelurahan');        // run                if ($this->notification->valid_input()) {                        if($this->kelurahanmodel->is_exists_kelurahan(strtolower($nama_kelurahan))) {                $this->notification->set_message('Nama kelurahan yang diinput di Kecamatan ini sudah ada');                $this->notification->clear_post();            }        }                if ($this->notification->valid_input()) {            $id_kecamatan = $this->input->post('id_kecamatan');            $data = $this->kelurahanmodel->get_kelurahan_by_id($id_kecamatan);                        if(!empty($data)):            $this->notification->set_message("Kode kelurahan yang dimasukkan sudah ada!");            $this->notification->sent_notification(false);            redirect('private/kelurahan/add/' . $this->input->post('id_kecamatan'));            endif;            // params            $params = array($this->input->post('id_kelurahan'),$this->input->post('id_kecamatan'), $this->input->post('nama_kelurahan'));                        if($this->kelurahanmodel->process_kelurahan_add($params)) {                               $this->notification->clear_post();                $this->notification->set_message("Data berhasil disimpan");                $this->notification->sent_notification(true);            } else {                $this->notification->set_message("Data gagal disimpan");                $this->notification->sent_notification(false);            }        } else {            $this->notification->sent_notification(false);        }        // default redirect        redirect('private/kelurahan/add/' . $this->input->post('id_kecamatan'));    }    public    function edit($id_kecamatan = '', $id_kelurahan = '') {        // template content        $this->smarty->assign("template_content", "private/kelurahan/kelurahan-edit");        // load        $this->load->model('kelurahanmodel');        $this->load->library('notification');        // assign variable        // url        $this->smarty->assign('url_add', site_url('private/kelurahan/add/' . $id_kecamatan));        $this->smarty->assign('url_list', site_url('private/kelurahan/index/' . $id_kecamatan));        $this->smarty->assign("url_process", site_url("private/kelurahan/process/edit"));        // data        $data = $this->kelurahanmodel->get_kelurahan_by_id($id_kelurahan);        $this->smarty->assign("data", $data);        $datakecamatan= $this->kelurahanmodel->get_all_kecamatan();        $this->smarty->assign("kecamatan", $datakecamatan);        $this->smarty->assign("kecamatan_selected", $id_kecamatan);        // notification        $arr_notify = $this->notification->get_notification();                if(!empty($arr_notify['post'])) {            $this->smarty->assign("data", $arr_notify['post']);        }        $this->smarty->assign("notification_msg", $arr_notify['message']);        $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':        'green'));        // display document        $this->parser->parse( 'private/base-layout/document.html');    }    public    function hapus($id_kecamatan = '', $id_kelurahan = '') {        // template content        $this->smarty->assign("template_content", "private/kelurahan/kelurahan-hapus");        // load        $this->load->model('kelurahanmodel');        $this->load->library('notification');        // assign variable        // url        $this->smarty->assign('url_add', site_url('private/kelurahan/add/' . $id_kecamatan));        $this->smarty->assign('url_list', site_url('private/kelurahan/index/' . $id_kecamatan));        $this->smarty->assign("url_process", site_url("private/kelurahan/process/hapus"));        // data        $data = $this->kelurahanmodel->get_kelurahan_by_id($id_kelurahan);        $this->smarty->assign("data", $data);        $datakecamatan = $this->kelurahanmodel->get_all_kecamatan();        $this->smarty->assign("kecamatan", $datakecamatan);        $this->smarty->assign("kecamatan_selected", $id_kecamatan);        // notification        $arr_notify = $this->notification->get_notification();                if(!empty($arr_notify['post'])) {            $this->smarty->assign("data", $arr_notify['post']);        }        $this->smarty->assign("notification_msg", $arr_notify['message']);        $this->smarty->assign("notification_status", (empty($arr_notify['message_status'])?'red':        'green'));        // display document        $this->parser->parse( 'private/base-layout/document.html');    }    public    function process_edit() {        // load library        $this->load->model('kelurahanmodel');        $this->load->library('notification');        // set rules        $this->notification->check_post('id_kelurahan', 'id_kelurahan', 'required');        $this->notification->check_post('id_kecamatan', 'Nama Kecamatan', 'required');        $this->notification->check_post('nama_kelurahan', 'Nama kelurahan', 'required');        // run                if ($this->notification->valid_input()) {            // params            $params = array($this->input->post('id_kecamatan'), $this->input->post('nama_kelurahan'), $this->input->post('id_kelurahan'));            // processing                        if($this->kelurahanmodel->process_kelurahan_edit($params)) {                               $this->notification->clear_post();                $this->notification->set_message("Data berhasil disimpan");                $this->notification->sent_notification(true);            } else {                $this->notification->set_message("Data gagal disimpan");                $this->notification->sent_notification(false);            }        } else {            $this->notification->sent_notification(false);        }        // default redirect        redirect('private/kelurahan/edit/' . $this->input->post('id_kecamatan') . '/' .$this->input->post('id_kelurahan'));    }    public    function process_hapus() {        // load library        $this->load->library('notification');        $this->load->model('kelurahanmodel');        // set rules        $this->notification->check_post('id_kecamatan', 'ID kelurahan', 'required');        // run        $id = $this->input->post('id_kelurahan');        $banyak = count($id);                if ($this->notification->valid_input()) {            $data = $this->kelurahanmodel->get_kelurahan_by_id($id_kelurahan);                        if($this->kelurahanmodel->process_kelurahan_delete_one($id)) {                            $this->notification->clear_post();                $this->notification->set_message($banyak.' Data berhasil dihapus');                $this->notification->sent_notification(true);            } else {                $this->notification->set_message("Data gagal dihapus");                $this->notification->sent_notification(false);            }        } else {            $this->notification->sent_notification(false);        }        // default redirect        redirect('private/kelurahan/index/' . $this->input->post('id_kecamatan'));    }}