<?php         if (!defined('BASEPATH')) exit('No direct script access allowed');    class Beritamodel extends CI_Model {                function  __construct() {            // Call the Model constructor            parent::__construct();        }                function get_list_asosiasi() {            $sql = "SELECT * FROM relawan_m ORDER BY id_relawan DESC";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return 0;            }        }       function get_list_relawan_private() {                       $sql = "SELECT * FROM relawan_m             ORDER BY id_relawan DESC";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return 0;            }        }                function get_total_berita() {                    $sql = "SELECT COUNT(*)'total' FROM berita_m WHERE id_relawan = 100";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return $result["total"];            } else {                return 0;            }        }                function get_list_berita_limit($params) {            $sql = "SELECT a.*, s.nama FROM berita_m a         LEFT JOIN relawan_m s ON a.id_relawan = s.id_relawan  where a.id_relawan = 100             ORDER BY id_relawan DESC LIMIT ?, ?";            $query = $this->db->query($sql,$params);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function get_list_berita_private() {                               $sql = "SELECT a.*, s.id_relawan, s.nama FROM berita_m a            LEFT JOIN relawan_m s ON a.id_relawan = s.id_relawan GROUP BY a.id_berita            ORDER BY a.tanggal DESC ";                     $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function get_list_berita_terkait() {            $sql = "SELECT a.*, s.id_relawan, s.nama FROM berita_m a         LEFT JOIN relawan_m s ON a.id_relawan = s.id_relawan WHERE s.id_relawan = 100            ORDER BY tanggal DESC LIMIT 0,3";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                                function get_berita_by_id($id) {            $this->db->where('id_berita', $id);            $query = $this->db->get('berita_m');                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function get_berita_relawan_home() {            $this->db->select('a.*,nama');            $this->db->where('a.id_relawan <', 100);            $this->db->join('relawan_m b','a.id_relawan = b.id_relawan');            $this->db->order_by("a.id_relawan","desc");            $query = $this->db->get('relawan_m a',2, 0);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }        function get_berita_reutama_home() {            $this->db->select('a.*,nama');            $this->db->where('a.id_relawan =', 100);            $this->db->join('relawan_m b','a.id_relawan = b.id_relawan');            $this->db->order_by("a.relawan","desc");            $query = $this->db->get('relawan_m a',3, 0);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function process_berita_add($params) {            $sql = "INSERT INTO berita_m (id_relawan, judul, content, tanggal, keterangan_gambar)                VALUES (?, ?, ?, ?, ?)";            return $this->db->query($sql, $params);        }                function process_berita_edit($params) {            $sql = "UPDATE berita_m SET id_relawan = ? , judul = ?, content = ?, tanggal = ?, keterangan_gambar = ?                 WHERE id_berita = ? ";            return $this->db->query($sql, $params);        }                function process_berita_delete($id) {            $this->db->where('id_berita', $id);            return $this->db->delete('berita_m');        }    }