<?php    class Dashboardmodel extends CI_Model {                function  __construct() {            // Call the Model constructor            parent::__construct();        }                        function get_total_album() {            $sql = "SELECT COUNT(*)'total_album' FROM album_m ";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return intval($result['total_album']);            } else {                return 0;            }        }                function get_total_foto() {            $sql = "SELECT COUNT(*)'total_foto' FROM foto_m ";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return intval($result['total_foto']);            } else {                return 0;            }        }                function get_total_video() {            $sql = "SELECT COUNT(*)'total_video' FROM video_m ";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return intval($result['total_video']);            } else {                return 0;            }        }                function get_total_informasi() {            $sql = "SELECT COUNT(*)'total_informasi' FROM informasi_m ";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return intval($result['total_informasi']);            } else {                return 0;            }        }                       function get_total_berita() {            $sql = "SELECT COUNT(*)'total_berita' FROM berita_m";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return intval($result['total_berita']);            } else {                return 0;            }        }         function get_total_berita_anggota() {            $sql = "SELECT COUNT(*)'total_berita' FROM berita_m where id_asosiasi <> '100'";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return intval($result['total_berita']);            } else {                return 0;            }        }                    }