<?php         if (!defined('BASEPATH')) exit('No direct script access allowed');    class Informasimodel extends CI_Model {                function  __construct() {            // Call the Model constructor            parent::__construct();        }                      function get_total_informasi() {                       $sql = "SELECT COUNT(*)'total' FROM informasi_m";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return $result["total"];            } else {                return 0;            }        }                function get_list_informasi_limit($params) {               $sql = "SELECT * from informasi_m ORDER BY id_informasi DESC LIMIT ?,? ";            $query = $this->db->query($sql,$params);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }         function get_list_informasi_home() {               $sql = "SELECT * from informasi_m ORDER BY tanggal DESC LIMIT 0,6";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }         function get_list_informasi_side() {               $sql = "SELECT * from informasi_m ORDER BY tanggal DESC LIMIT 0,3";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }        function get_list_informasi() {               $sql = "SELECT * from informasi_m ORDER BY tanggal DESC";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function get_list_informasi_terkait() {                       $id_informasi = $this->uri->segment(4,0);            $sql = "SELECT * FROM informasi_m         ORDER BY tanggal DESC LIMIT 0,5";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }        function get_list_informasi_terkait_kedua() {                       $id_informasi = $this->uri->segment(4,0);            $sql = "SELECT * FROM informasi_m         ORDER BY tanggal DESC LIMIT 5,5";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function get_list_informasi_limit_private() {            $sql = "SELECT * from informasi_m a ORDER BY tanggal DESC";            $query = $this->db->query($sql);                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return false;            }        }                function get_informasi_by_id($id) {            $this->db->where('id_informasi', $id);            $query = $this->db->get('informasi_m');                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return $result;            } else {                return false;            }        }                               function process_informasi_add($params) {            $sql = "INSERT INTO informasi_m (judul, content, tanggal, keterangan_gambar)                VALUES (?, ?, ?, ?)";            return $this->db->query($sql, $params);        }                function process_informasi_edit($params) {            $sql = "UPDATE informasi_m SET  judul = ?, content = ?, tanggal = ? ,keterangan_gambar = ?                 WHERE id_informasi = ? ";            return $this->db->query($sql, $params);        }                function process_informasi_delete($id) {            $this->db->where('id_informasi', $id);            return $this->db->delete('informasi_m');        }    }