<?php    class Searchmodel extends CI_Model {                function  __construct() {            // Call the Model constructor            parent::__construct();        }        // get total hasil pencarian                 function get_total_pencarian() {            $sql = "SELECT COUNT(*) as total from berita_m";            $query = $this->db->query($sql);            //echo $this->db->last_query(); exit;                        if ($query->num_rows() > 0) {                $result = $query->row_array();                $query->free_result();                return $result["total"];            } else {                return 0;            }        }                function get_pencarian_berita_indo($params) {            $sql = "SELECT a.*, s.nama FROM berita_m a LEFT JOIN relawan_m s ON a.id_relawan = s.id_relawan   WHERE a.judul LIKE ? or a.content LIKE ?";            $query = $this->db->query($sql, $params);            //echo $this->db->last_query(); exit;                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return 0;            }        }                               function get_pencarian_informasi_indo($params) {            $sql = "SELECT * FROM informasi_m   WHERE judul LIKE ? or content LIKE ?";            $query = $this->db->query($sql, $params);            //echo $this->db->last_query(); exit;                        if ($query->num_rows() > 0) {                $result = $query->result_array();                $query->free_result();                return $result;            } else {                return 0;            }        }                                  }